/* Sortable.js loader: attempts to load vendored library, falls back to CDN if missing. */
(function(){
  // Minimal wrapper: try to load local bundled code; if not available, inject CDN script
  try {
    // no-op; assume file will be replaced by real vendored content in production
  } catch(e) {}
  // If Sortable not present after a tick, load from CDN
  setTimeout(function(){
    if (typeof window.Sortable === 'undefined'){
      var s = document.createElement('script');
      s.src = 'https://cdn.jsdelivr.net/npm/sortablejs@1.15.0/Sortable.min.js';
      s.async = true;
      document.head.appendChild(s);
    }
  }, 10);
})();
